function isOldPinned(t) {
  const pinned = t.json.pinned === true || t.json.pinned_globally === true;
  if (!pinned) return false;
  const created = new Date(t.json.created);
  const now = new Date();
  const days = (now - created) / (1000*60*60*24);
  return days > 365;
}

const list = items.map(i => i.json);
const filtered = list.filter(t => !isOldPinned({ json: t }));
filtered.sort((a,b) => new Date(b.bumped_at) - new Date(a.bumped_at));
return filtered.slice(0,4).map(t => ({ json: t }));

return items.map(item => {
  const topic = item.json;
  const first = topic.post_stream?.posts?.find(p => p.post_number === 1);

  return {
    json: {
      id: topic.id,
      title: topic.title,
      url: topic.slug ? `https://forum.livepeer.org/t/${topic.slug}/${topic.id}` : topic.url,
      excerpt: first?.cooked || '',
      author: first?.username || first?.name || null
    }
  };
});

const topics = items[0].json.topics;
let mdx = `## Top Weekly Forum Topics

`;

for (const t of topics) {
  mdx += `### [${t.title}](${t.url})

`;
  mdx += `_${t.author || 'Unknown'}_

`;
  mdx += `${t.excerpt.slice(0,200).replace(/
/g,' ')}

`;
}

return [{ json: { fileContent: mdx } }];
